import { useState } from 'react'
import { Navbar , Nav, Container} from 'react-bootstrap'
import Home from './home';
import About from './about';


const Header = ()=>{

    const [showHome, setShowHome] = useState('');
    const [showAbout, setShowAbout] = useState('');
    const [showSkills, setShowSkills] = useState('');

    const handleClickHome = ()=>{
      setShowHome(!showHome);
    }
    const handleClickAbout = ()=>{
      setShowAbout(!showAbout);
    }
    const handleClickSkills = ()=>{
      setShowSkills(!showSkills);
    }

  return(

<>

<Navbar collapseOnSelect expand="lg" bg="info" variant="dark">
      <Container>
        <Navbar.Brand href="#home">My Portfolio</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="ml-auto float-end">
            <Nav.Link href="#home" onClick={handleClickHome}>Home</Nav.Link>
            <Nav.Link href="#about" onClick={handleClickAbout}>About</Nav.Link>
            <Nav.Link href="#skills" onClick={handleClickSkills}>Skills</Nav.Link>
            <Nav.Link href="#projects">Projects</Nav.Link>
            <Nav.Link href="#contact">Contact</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    { showHome && <Home/>}
    { showAbout && <About/>}
</>

  )
}
export default Header
